import React, { Component } from 'react'
import './orderDetails.css'

class OrderDetails extends Component {
    constructor(props) {
        super(props)

        this.state = {
             email : '',
             phone: '',
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange(e) {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        const postData = {
            email: this.state.email
        }
        console.log("before", JSON.stringify(postData))
        fetch(`https://83fb21f6.ngrok.io/orders/${this.props.location.pathname.split('/')[2]}`,{
            method: 'PUT',
            headers : {
                "content-type": "application/json"
            },
            body: JSON.stringify(postData)
        })
        .then(res => console.log(res))
        .catch(err => console.log(err))
    }

    render() {

        //Destructuring the state objects
        const { email, phone }  = this.state

        return (
            <div>
                {/* Navbar */}
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <div className="container">
                        <a className="navbar-brand" href="/">Shopify App</a>
                    </div>
                </nav>
                {console.log(this.props)}
                &nbsp; &nbsp; &nbsp;
                <div className="container">
                    <div className="layout_item">
                        <form onSubmit={this.onSubmit} >
                            {/* Contact Information */}
                            <h3>Contact Information</h3> <br />
                            <div className="row">
                                <div className="col-sm-6">
                                    <div className="form-group">
                                        <label htmlFor="first_name">First Name</label>
                                        <input type="text"className="form-control" name="first_name" onChange={this.onChange} value={email} />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="last_name">Last Name</label>
                                        <input type="number" className="form-control" name="last_name" onChange={this.onChange} value={phone} />
                                    </div>
                                </div>

                                <div className="col-sm-6">
                                    <div className="form-group">
                                        <label htmlFor="email">Email address</label>
                                        <input type="email"className="form-control" name="email" onChange={this.onChange} value={email} />
                                    </div>

                                    <div className="form-group">
                                        <label htmlFor="email">Phone</label>
                                        <input type="number" className="form-control" name="phone" onChange={this.onChange} value={phone} />
                                    </div>
                                </div>


                            </div>

                            {/* Shipping Address */}
                            <div className="col-sm-6">
                                <div className="form-group">
                                    <label htmlFor="email">Email address</label>
                                    <input type="email"className="form-control" name="email" onChange={this.onChange} value={email} />
                                </div>

                                <div className="form-group">
                                    <label htmlFor="email">Phone</label>
                                    <input type="number" className="form-control" name="phone" onChange={this.onChange} value={phone} />
                                </div>
                            </div>
                            <button type="submit" className="btn-primary btn-sm">Save</button>
                        </form>
                        &nbsp; &nbsp; &nbsp;
                    </div>
                </div>
            </div>
        )
    }
}

export default OrderDetails
