import React, { Component } from 'react'
import './orderDetails.css'

class OrderDetails extends Component {
    constructor(props) {
        super(props)

        this.state = {
             email : '',
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChange(e) {
        this.setState({
            [e.target.name] : e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();
        const postData = {
            email: this.state.email
        }
        console.log("before", JSON.stringify(postData))
        fetch(`https://83fb21f6.ngrok.io/orders/${this.props.location.pathname.split('/')[2]}`,{
            method: 'PUT',
            headers : {
                "content-type": "application/json"
            },
            body: JSON.stringify(postData)
        })
        .then(res => console.log(res))
        .catch(err => console.log(err))
    }

    render() {

        //Destructuring the state objects
        const { email }  = this.state

        return (
            <div>
                {/* Navbar */}
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <div className="container">
                        <a className="navbar-brand" href="/">Shopify App</a>
                    </div>
                </nav>
                {console.log(this.props.location.pathname.split('/')[2])}
                &nbsp; &nbsp; &nbsp;
                <div className="container">
                    <div className="layout_item">
                        <form onSubmit={this.onSubmit} >
                            <input type="email" name="email" onChange={this.onChange} value={email} required />
                            <button type="submit">Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        )
    }
}

export default OrderDetails
