import React, { Component } from 'react'

class Orders extends Component {
    constructor(props) {
        super(props)

        this.state = {
             order: [],
        }
    }

    componentDidMount(){
        fetch("https://8aecb531.ngrok.io/orders")
        .then(res => res.json())
        .then(res => this.setState({
            orders: res
        }))
    }


    render() {

        const orderData = this.state.orders.map((order)=>{
            return order
        });
        return (
            <div>
                {/* Navbar */}
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <div className="container">
                        <a className="navbar-brand" href="#">Shopify App</a>
                    </div>
                </nav>
                &nbsp; &nbsp; &nbsp;

                {/* Orders */}
                <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="card text-center">
                                <div className="card-title"><a href="/orders">Orders</a></div>
                                {this.state.orders}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Orders
