import React, { Component } from 'react'
import './orders.css'

class Orders extends Component {
    constructor(props) {
        super(props)

        this.state = {
             orderData: [],
        }
    }

    componentDidMount(){
        // const response = await fetch("https://8aecb531.ngrok.io/orders")
        // const data = response.json()
        // console.log(data)
        // this.setState({orderData: data})
        fetch("https://31197e82.ngrok.io/orders") 
        .then(res => res.json())
        .then(res => this.setState({orderData: res}))
    }



    render() {
        let data = []
        this.state.orderData.map((orders,index) => {
            return (
                <div key={index}>
                    {data.push(orders)}
                </div>
            ) 
        })

        return (
            <div>
                {/* Navbar */}
                <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                    <div className="container">
                        <a className="navbar-brand" href="/">Shopify App</a>
                    </div>
                </nav>
                &nbsp; &nbsp; &nbsp;

                {/* Orders */}
                <div className="container-fluid">
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="text-center">
                                <h3>Orders</h3>
                                {console.log(data)}
                            </div>
                            <table>
                                <thead>
                                    <tr>
                                        <th>#</th>
                                        <th>Order Number</th>
                                        <th>Total</th>
                                        <th>Number of Items</th>
                                        <th>Customer</th>
                                        <th>Shipping Address</th>
                                        <th>Billing Address</th>
                                        <th>Date of Purchase</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                {data.map((item,index) => {
                                    return (
                                        <div>
                                            <td>{index}</td>
                                            <td>{item.order_number}</td>
                                            <td>{item.total_price}</td>
                                        </div>
                                    )
                                })}
                                </tr>
                            </tbody>
                            </table>

                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Orders
